function table.merge(t1, t2)
  for k,v in ipairs(t2) do
    table.insert(t1, v)
  end

  return t1
end

function table.keyedMerge(t1, t2)
  for k, v in pairs(t2) do
    t1[k] = v
  end

  return t1
end

-- Only add modded items when the mod is also loaded
if mods["5dim_ores"] then
  valid_artillery_items = table.merge(valid_artillery_items, fivedim_valid_artillery_items)
end

if mods.bobores then
  valid_artillery_items = table.merge(valid_artillery_items, bobores_valid_artillery_items)
  custom_tints = table.keyedMerge(custom_tints, bobores_custom_tints)
end

if mods.angelsrefining then
  valid_artillery_items = table.merge(valid_artillery_items, angelsrefining_valid_artillery_items)
end

if mods.hardCrafting then
  valid_artillery_items = table.merge(valid_artillery_items, hardCrafting_valid_artillery_items)
end

